calinski<- function(tab,fac){		tab <- as.matrix(tab)        tab <- apply(tab,2,scale,center=TRUE,scale=FALSE)	fac <- as.factor(fac)	nlev <- length(levels(fac))	n <- nrow(tab)        mean.f1 <- function(x,fac) {          z <- tapply(x, fac, mean)          return(z)        }        tabmoy <- apply(tab,2,mean.f1,fac = fac)                ni <- tapply(rep(1,n),fac,sum)                tabwit <- tab - tabmoy[fac, ]        SST <- sum(tab^2)        SSW <- sum(tabwit^2)        SSB <- sum(sweep(tabmoy^2,1,ni,"*"))                Calinski<-((SSB*(n-nlev))/(SSW*(nlev-1)))        Hartigan<-log(SSB/SSW)		        return(c(calinski=Calinski,hartigan=Hartigan))	}